cked", false);                    $("#pages_per_sheet_div").show();                        $("#pages_per_sheet_ppt_landscape_div").hide();                        $("#pages_per_sheet_ppt_portrait_div").show();                }            }        }        //$("#pages_per_sheet_div").hide();        //$("#landscape_or_portrait_div").hide();        $('#page_number_single').change(function(){            var data = $('#page_number_single').val();            var arr = data.split(',');            for (i = 0; i < arr.length; i++) {                if(isNaN(parseInt(arr[i])) || parseInt(arr[i])<=0){                    alert("Please chose a number greater than zero");                    $('#page_number_single').val("");                    break;                }            }        });                $('#uploaded_file:file').change(function(){            var fileSize = 0;//in bytes            var str = "";            for(i=0; i<$('#uploaded_file:file')[0].files.length; i++){                var file = $('#uploaded_file:file')[0].files[i];                fileSize += file.size;                var txt ="<button id=\""+file.name+"\" type=\"button\" class=\"btn btn-default added_files\" aria-label=\"Left Align\"><span style=\"color:green\" class=\"glyphicon glyphicon-ok-sign\" aria-hidden=\"true\"></span>"+file.name+"</button>";                str = str.concat(txt);            }            var maxSize = $('#uploaded_file:file').data('file-maxsize');            if(fileSize>maxSize){                var alert_txt = "<br><div class=\"alert alert-danger added_files\" role=\"alert\"><span class=\"glyphicon glyphicon-exclamation-sign\" aria-hidden=\"true\"></span><span class=\"sr-only\">Error:</span>File size exceed 25MB</div>";                str = alert_txt.concat(str);                str = str.replace(/green/g,"red");                str = str.replace(/glyphicon-ok-sign/g,"glyphicon-remove-sign");                $("#cid_22").append(str);                $('#uploaded_file:file').val("");                            }else{                $("#cid_22").append(str);                //alert('file size is correct- '+fileSize+' bytes');            }                });        $('#uploaded_file:file').click(function(){                $(".added_files").remove();        });        /*$("#cid_22").on("click", ".added_files", function(e){            var file_to_remove = $(e.currentTarget).attr('id');            for(i=0; i<$('#uploaded_file:file')[0].files.length; i++){                var file = $('#uploaded_file:file')[0].files[i];                if(file_to_remove===file.name){                    $('#uploaded_file:file')[0].files[i].remove();                    //alert("File found!");                }            }            $(e.currentTarget).remove();           });*/                $('#page_number_from').change(function(){            if($('#page_number_from').val()<=0){                alert("Please chose a number greater than zero");                $('#page_number_from').val("");            }            else if(parseInt($('#page_number_to').val())<=parseInt($('#page_number_from').val())){                if($('#page_number_to').val()!==""){                    alert("From should be smaller than To");                    $('#page_number_from').val("");                }            }        });        $('#page_number_to').change(function(){            if($('#page_number_to').val()<=0){                alert("Please chose a number greater than zero");                $('#page_number_to').val("");            }            else if(parseInt($('#page_number_to').val()) <= parseInt($('#page_number_from').val())){                if($('#page_number_from').val()!==""){                    alert("From should be smaller than To");                    $('#page_number_to').val("");                }            }        });        $("input[name=page_to_print]").change(function(){            //alert($("input[name=page_to_print]:checked").val());            if($('input[name=page_to_print]:checked').val()==="one_page"){                $('#page_number_single').attr("required",true);                $('#page_number_from').attr("disabled",true);                $('#page_number_to').attr("disabled",true);                $('#page_number_single').attr("disabled",false);                $('#page_number_from').val("");                $('#page_number_to').val("");            }            else if($('input[name=page_to_print]:checked').val()==="from_to"){                $('#page_number_single').attr("required",false);                $('#page_number_single').val("");                $('#page_number_from').attr("required",true);                $('#page_number_to').attr("required",true);                $('#page_number_from').attr("disabled",false);                $('#page_number_to').attr("disabled",false);                $('#page_number_single').attr("disabled",true);            }            else if($('input[name=page_to_print]:checked').val()==="all"){                $('#page_number_from').attr("required",false);                $('#page_number_to').attr("required",false);                $('#page_number_single').attr("required",false);                $('#page_number_single').val("");                $('#page_number_from').val("");                $('#page_number_to').val("");                $('#page_number_from').attr("disabled",true);                $('#page_number_to').attr("disabled",true);                $('#page_number_single').attr("disabled",true);            }        });        $("#landscape_or_portrait").change(function(){            if($('#landscape_or_portrait').val() ===""){                //$("#landscape_or_portrait_div").hide();                //$("#landscape_or_portrait").val("");                $("#pages_per_sheet_div").hide();                $('input[name=pages_per_sheet]:checked').prop("checked", false);            }            else{                if($('#landscape_or_portrait').val() ==="landscape"){                    $('input[name=pages_per_sheet]:checked').prop("checked", false);                    $("#pages_per_sheet_div").show();                        $("#pages_per_sheet_ppt_landscape_div").show();                        $("#pages_per_sheet_ppt_portrait_div").hide();                }                else if($('#landscape_or_portrait').val() ==="portrait"){                    $('input[name=pages_per_sheet]:checked').prop("checked", false);                    $("#pages_per_sheet_div").show();                        $("#pages_per_sheet_ppt_landscape_div").hide();                        $("#pages_per_sheet_ppt_portrait_div").show();                }            }        });                $('#email_form_with_php').submit(function(){            $("#email_form_with_php").hide();            //$("#input_1").hide();            $("#gif").show();        });                $("#button").click(function() {            $('html, body').animate({                scrollTop: $("#contact_us").offset().top            }, 2000);        });    });</script><script>        function logOrderSubmittedEvent() {        try {            var colort = document.getElementById('input_19_1').value;            if (document.getElementById('input_19_0').checked) {                colort = document.getElementById('input_19_0').value;            }            var e = document.getElementById('input_23');            var psize = e.options[e.selectedIndex].value;            e = document.getElementById('word_or_presentation');            var word_pres = e.options[e.selectedIndex].value;            e = document.getElementById('landscape_or_portrait');            var orient = e.options[e.selectedIndex].value;            var params = {};            params['BlackColour'] = colort;            params['PageSize