        }        });        $("input[name=page_to_print]").change(function(){            //alert($("input[name=page_to_print]:checked").val());           if($('input[name=page_to_print]:checked').val()==="one_page"){               $('#page_number_single').attr("required",true);               $('#page_number_from').attr("disabled",true);               $('#page_number_to').attr("disabled",true);               $('#page_number_single').attr("disabled",false);               $('#page_number_from').val("");               $('#page_number_to').val("");           }           else if($('input[name=page_to_print]:checked').val()==="from_to"){               $('#page_number_single').attr("required",false);               $('#page_number_single').val("");               $('#page_number_from').attr("required",true);               $('#page_number_to').attr("required",true);               $('#page_number_from').attr("disabled",false);               $('#page_number_to').attr("disabled",false);               $('#page_number_single').attr("disabled",true);           }           else if($('input[name=page_to_print]:checked').val()==="all"){               $('#page_number_from').attr("required",false);               $('#page_number_to').attr("required",false);               $('#page_number_single').attr("required",false);               $('#page_number_single').val("");               $('#page_number_from').val("");               $('#page_number_to').val("");               $('#page_number_from').attr("disabled",true);               $('#page_number_to').attr("disabled",true);               $('#page_number_single').attr("disabled",true);           }                   });        $("#landscape_or_portrait,#word_or_presentation").change(function(){            if($('#word_or_presentation').val() ===""){                $("#landscape_or_portrait_div").hide();                $("#landscape_or_portrait").val("");                $("#pages_per_sheet_div").hide();                $('input[name=pages_per_sheet]:checked').prop("checked", false);            }            else{                $("#landscape_or_portrait_div").show();            }             $('input[name=pages_per_sheet]:checked').prop("checked", false);             $("#pages_per_sheet_div").hide();            if($('#landscape_or_portrait').val() ===""){                $("#pages_per_sheet_div").hide();                $('input[name=pages_per_sheet]:checked').prop("checked", false);            }            else{                if($('#landscape_or_portrait').val() ==="landscape"){                    $('input[name=pages_per_sheet]:checked').prop("checked", false);                    $("#pages_per_sheet_div").show();                    if($('#word_or_presentation').val() ==="word"){                        $("#pages_per_sheet_word_landscape_div").show();                        $("#pages_per_sheet_word_portrait_div").hide();                        $("#pages_per_sheet_ppt_landscape_div").hide();                        $("#pages_per_sheet_ppt_portrait_div").hide();                    }                    else if($('#word_or_presentation').val() ==="presentation"){                        $("#pages_per_sheet_word_landscape_div").hide();                        $("#pages_per_sheet_word_portrait_div").hide();                        $("#pages_per_sheet_ppt_landscape_div").show();                        $("#pages_per_sheet_ppt_portrait_div").hide();                    }                                    }                else if($('#landscape_or_portrait').val() ==="portrait"){                    $('input[name=pages_per_sheet]:checked').prop("checked", false);                    $("#pages_per_sheet_div").show();                    if($('#word_or_presentation').val() ==="word"){                        $("#pages_per_sheet_word_landscape_div").hide();                        $("#pages_per_sheet_word_portrait_div").show();                        $("#pages_per_sheet_ppt_landscape_div").hide();                        $("#pages_per_sheet_ppt_portrait_div").hide();                    }                    else if($('#word_or_presentation').val() ==="presentation"){                        $("#pages_per_sheet_word_landscape_div").hide();                        $("#pages_per_sheet_word_portrait_div").hide();                        $("#pages_per_sheet_ppt_landscape_div").hide();                        $("#pages_per_sheet_ppt_portrait_div").show();                    }                }            }        });    });  </script></body></html>